mapper.select:
SELECT CC.seq AS CCseq ,CCG.seq AS CCGseq ,CCG.groupName AS CCGname ,CCG.groupName_code AS CCGname_code ,CCG.groupName_en AS CCGname_en ,CCG.delYn AS CCGdelYn ,CC.codeName_code AS CCname_code ,CC.codeName AS CCname ,CC.regdate AS CCregdate ,CC.delYn AS CCdelYn FROM codeGroup CCG LEFT JOIN code CC ON CCG.seq = CC.group_seq WHERE 1=1
AND CC.delYn is not null AND CC.delYn = #{shDelYn}
AND groupName_code Like CONCAT('%',#{shValue},'%') AND groupName Like CONCAT('%',#{shValue},'%') AND codeName_code Like CONCAT('%',#{shValue},'%') AND codeName Like CONCAT('%',#{shValue},'%')
ORDER BY CC.seq ASC limit #{rowNumToShow} offset #{startRnumForMysql}

mapper.insert:
	INSERT INTO code(
		group_seq
    	,codeName_code
    	,codeName
    	,regdate
    	,delYn
	)

	VALUES (
		#{group_seq}
		,#{CCname_code}
		,#{CCname}
		,NOW()
		,1
	)

	SELECT last_insert_id()

mapper.update:
UPDATE code
SET 
	group_seq = #{group_seq}
	,codeName_code = #{CCname_code}
	,codeName = #{CCname}
	,delYn = #{delYn}
WHERE
	seq = #{seq}

mapper.select:
SELECT count(*) FROM codeGroup CCG LEFT JOIN code CC ON CCG.seq = CC.group_seq WHERE 1=1
AND CC.delYn is not null AND CC.delYn = #{shDelYn}
AND groupName_code Like CONCAT('%',#{shValue},'%') AND groupName Like CONCAT('%',#{shValue},'%') AND codeName_code Like CONCAT('%',#{shValue},'%') AND codeName Like CONCAT('%',#{shValue},'%')
