mapper.sql:
    	FROM codeGroup CCG
    	WHERE 1=1


				AND CCG.delYn = #{shDelYn}




			AND CCG.groupName_code LIKE CONCAT('%',#{shValue},'%')
			AND CCG.groupName LIKE CONCAT('%',#{shValue},'%')
			AND CCG.groupName_en LIKE CONCAT('%',#{shValue},'%')



			AND CCG.regdate between #{shdate_s} and #{shdate_e}
			AND CCG.moddate between #{shdate_s} and #{shdate_e}

mapper.insert:
	INSERT INTO codeGroup(
		groupName_code
    	,groupName
    	,groupName_en
    	,regdate
    	,useYn
    	,delYn
	)

	VALUES (
		#{groupName_code}
		,#{groupName}
		,#{groupName_en}
		,NOW()
		,#{useYn}
		,#{delYn}
	)

	SELECT last_insert_id()

mapper.update:
UPDATE codeGroup
SET 
	groupName_code = #{groupName_code}
	,groupName_en = #{groupName_en}
	,groupName = #{groupName}
	,useYn = #{useYn}
	,delYn = #{delYn}
WHERE
	seq = #{seq}
