mapper.insert:
	INSERT INTO product(
    	category
    	,title
    	,price
    	,stock
	)

	VALUES (
		#{category}
		,#{title}
		,#{price}
		,#{stock}
	)

	SELECT last_insert_id()

INSERT INTO review
(
	,member_seq
	,product_seq
	,content as ifprReviewContent
	,regDate as ifprReviewRegDate
	,star
)
VALUES(
	,#{member_seq}
	,#{ifprSeq}
	,#{ifprReviewContent}
	,NOW()
	,#{star}
)

SELECT last_insert_id()

		INSERT INTO qa
		(
			member_seq
   	 		,product_seq
   	 		,answerYn
   	 		,ifprQatitle 
   	 		,content 
    		,regdate 
		)

		VALUES (
			#{member_seq}
			,#{ifprSeq}
			,0
			,#{ifprQaTitle}
			,#{ifprQaContent}
			,NOW()
		)


		SELECT last_insert_id()

mapper.update:
UPDATE product
SET
	title = #{title}
	,price = #{price}
	,stock = #{stock}
	,mainYn = #{mainYn}
	,ifprUseYn = #{ifprUseYn}
	,ifprDelYn = #{ifprDelYn}
WHERE 1=1
AND ifprSeq = #{ifprSeq}

mapper.insert:
	INSERT INTO productOption(
    	,product_seq
    	,optionMain
    	,optionSub
	)

	VALUES (
		#{ifprSeq}
		,#{optionMain}
		,#{optionSub}
	)

	SELECT last_insert_id()

mapper.update:
UPDATE productOption
SET
	optionMain = #{optionMain}
	,optionSub = #{optionSub}
WHERE 1=1
AND ifprSeq = #{ifprSeq}
